// Решение 8 задачи Эйлера
public class Problem8 {
    public static void main(String[] args) {
        // строка с 1000-значным числом
        String megaNumberString = "";
        // Количество подряд идущих цифр для произведения
        int nDigit = 13;
        // Переменная которая будет хранить максимальное произведение
        long maxNDigitProduct = 1;
        // Проход по всем подстрокам длинны nDigit
        for(int i=0;i<megaNumberString.length()-nDigit;i++){
            // Считаем произведение подряд идущих цифр
            long nDigitProduct = digitProduct(megaNumberString.substring(i, i + nDigit));
            // Если оно больше maxNDigitProduct, приравниваем maxNDigitProduct = nDigitProduct
            if (nDigitProduct > maxNDigitProduct)
                maxNDigitProduct = nDigitProduct;
        }
        System.out.println(maxNDigitProduct);
    }
    // Функция перемножает все цифры в строке
    public static long digitProduct(String stringNum){
        // Переменная для хранения произведения всех цифр
        long digitProd = 1;
        // Каждый символ в строке преобразуем в цифру и умножим на нее digitProd
        for(char digit: stringNum.toCharArray())
            digitProd *= (int)digit - (int)'0';
        return digitProd;
    }
}